    setSigner(null);
    setSessionStatus(null);
    setNftBalance(0);
  };

  // Ê£ÄÊü•Áî®Êà∑Áä∂ÊÄÅ
  const checkUserStatus = async () => {
    if (!userAddress) return;

    try {
      const session = await getSessionStatus(userAddress);
      setSessionStatus(session);

      const balance = await getNFTBalance(userAddress);
      setNftBalance(balance);

      if (session.isActive) {
        setSessionMode('session');
      } else if (balance > 0) {
        setSessionMode('nft');
      } else {
        setSessionMode('nft');
      }
    } catch (error) {
      console.error('Ê£ÄÊü•Áî®Êà∑Áä∂ÊÄÅÂ§±Ë¥•:', error);
    }
  };

  useEffect(() => {
