import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { mintNFTDirect, startGasSession as startRealGasSession, getSessionStatus, getNFTBalance } from './utils/erc4337';
import './App.css';

// Á°¨ÁºñÁ†ÅÁöÑËßÑÂàôÔºöÊåÅÊúâÁâπÂÆö NFT ÁöÑÂú∞ÂùÄ
const ELIGIBLE_ADDRESSES = [
  '0x742d35Cc6634C0532925a3b8D4C9db96C4b4d8b6', // Á§∫‰æãÂú∞ÂùÄ
  '0x1234567890123456789012345678901234567890', // Á§∫‰æãÂú∞ÂùÄ
];

// ÁßªÈô§‰∫ÜÊú™‰ΩøÁî®ÁöÑ DEMO_NFT_ADDRESS Â∏∏Èáè

// ‰ªªÂä°ÈÖçÁΩÆ
const TASKS = [
  {
    id: 1,
    title: 'ÂÖ≥Ê≥® GasMorph ÂÆòÊñπÊé®Áâπ',
    description: 'ÂÖ≥Ê≥® @GasMorph ÂÆòÊñπÊé®ÁâπË¥¶Âè∑',
    type: 'social',
    completed: false
  },
  {
    id: 2,
    title: 'Âä†ÂÖ• Discord Á§æÂå∫',
    description: 'Âä†ÂÖ• GasMorph Discord ÊúçÂä°Âô®',
    type: 'social',
    completed: false
  },
  {
    id: 3,
    title: 'ÂàÜ‰∫´È°πÁõÆ‰ªãÁªç',
    description: 'Âú®Á§æ‰∫§Â™í‰ΩìÂàÜ‰∫´ GasMorph È°πÁõÆ‰ªãÁªç',
    type: 'social',
    completed: false
  }
];

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [userAddress, setUserAddress] = useState<string>('');
  const [, setProvider] = useState<ethers.BrowserProvider | null>(null);
  const [signer, setSigner] = useState<ethers.Signer | null>(null);
  const [sessionActive, setSessionActive] = useState(false);
  const [sessionEndTime, setSessionEndTime] = useState<number>(0);
  const [remainingTime, setRemainingTime] = useState<string>('');
  const [mintCount, setMintCount] = useState(0);
  const [isMinting, setIsMinting] = useState(false);
  const [mintHistory, setMintHistory] = useState<Array<{timestamp: number, hash: string, gasSponsored: boolean}>>([]);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [tasks] = useState(TASKS);
  const [completedTasks, setCompletedTasks] = useState<number[]>([]);
  const [isEligible, setIsEligible] = useState<boolean>(false);
  const [nftBalance, setNftBalance] = useState<number>(0);

  // Ê£ÄÊü•Áî®Êà∑ÊòØÂê¶ÊúâËµÑÊ†º‰∫´Âèó Gas Ë°•Ë¥¥ÔºàÂü∫‰∫é NFT ÊåÅÊúâÔºâ
  const checkEligibility = async (address: string): Promise<boolean> => {
    try {
      const nftBalance = await getNFTBalance(address);
      return nftBalance > 0; // ÊåÅÊúâ NFT Âç≥ÊúâËµÑÊ†º
    } catch (error) {
      console.error('Ê£ÄÊü•ËµÑÊ†ºÂ§±Ë¥•:', error);
      return ELIGIBLE_ADDRESSES.includes(address.toLowerCase()); // ÈôçÁ∫ßÂà∞Á°¨ÁºñÁ†ÅÊ£ÄÊü•
    }
  };

  // Ê£ÄÊü•‰ªªÂä°ÂÆåÊàêÁä∂ÊÄÅ
  const checkTaskCompletion = (): boolean => {
    return completedTasks.length >= 2; // Ëá≥Â∞ëÂÆåÊàê2‰∏™‰ªªÂä°
  };

  // ËøûÊé•Èí±ÂåÖ
  const handleConnect = async () => {
    setError('');
    setSuccess('');

    if (typeof window !== 'undefined' && (window as any).ethereum) {
      try {
        console.log('Ê≠£Âú®ËøûÊé•Èí±ÂåÖ...');
        const accounts = await (window as any).ethereum.request({
          method: 'eth_requestAccounts',
        });

        if (accounts.length > 0) {
          const address = accounts[0];
          console.log('Èí±ÂåÖÂú∞ÂùÄ:', address);

          const prov = new ethers.BrowserProvider((window as any).ethereum);
          const signerInstance = await prov.getSigner();

          // Ê£ÄÊü•ÁΩëÁªú
          const network = await prov.getNetwork();
          console.log('ÂΩìÂâçÁΩëÁªú Chain ID:', network.chainId);

          // Â∞Ü BigInt ËΩ¨Êç¢‰∏∫ Number ËøõË°åÊØîËæÉ
          const chainId = Number(network.chainId);
          if (chainId !== 1337) {
            setError(`‚ö†Ô∏è ËØ∑ÂàáÊç¢Âà∞ Hardhat Êú¨Âú∞ÁΩëÁªú (Chain ID: 1337)ÔºåÂΩìÂâç: ${chainId}`);
            return;
          }

          setUserAddress(address);
          setProvider(prov);
          setSigner(signerInstance);
          setIsConnected(true);

          // Ê£ÄÊü•Áî®Êà∑ËµÑÊ†ºÂíå NFT ‰ΩôÈ¢ù
          try {
            const eligible = await checkEligibility(address);
            const balance = await getNFTBalance(address);
            setIsEligible(eligible);
            setNftBalance(balance);

            // Ê£ÄÊü•ÂΩìÂâç Gas Session Áä∂ÊÄÅ
            const sessionStatus = await getSessionStatus(address);
            if (sessionStatus.isActive) {
              setSessionActive(true);
              setSessionEndTime(sessionStatus.expiryTime);
            }

            setSuccess(`‚úÖ Èí±ÂåÖËøûÊé•ÊàêÂäüÔºÅ${eligible ? ' ÊÇ®ÊåÅÊúâ NFTÔºå‰∫´Âèó Gas Ë°•Ë¥¥ËµÑÊ†ºÔºÅ' : ''}`);
          } catch (error) {
            console.error('Ê£ÄÊü•Áî®Êà∑Áä∂ÊÄÅÂ§±Ë¥•:', error);
            setSuccess('Èí±ÂåÖËøûÊé•ÊàêÂäüÔºÅ');
          }

          console.log('Èí±ÂåÖËøûÊé•ÊàêÂäü');
        }
      } catch (error: any) {
        console.error('ËøûÊé•Èí±ÂåÖÂ§±Ë¥•:', error);

        if (error.code === 4001) {
          setError('Áî®Êà∑ÊãíÁªù‰∫ÜËøûÊé•ËØ∑Ê±ÇÔºåËØ∑ÈáçÊñ∞Â∞ùËØïËøûÊé•Èí±ÂåÖ');
        } else if (error.code === -32002) {
          setError('MetaMask Â∑≤ÁªèÂú®Â§ÑÁêÜËøûÊé•ËØ∑Ê±ÇÔºåËØ∑Ê£ÄÊü• MetaMask Á™óÂè£');
        } else {
          setError(error.message || 'ËøûÊé•Èí±ÂåÖÂ§±Ë¥•');
        }
      }
    } else {
      setError('ËØ∑ÂÆâË£Ö MetaMask Èí±ÂåÖ');
    }
  };

  // Êñ≠ÂºÄËøûÊé•
  const handleDisconnect = () => {
    setIsConnected(false);
    setUserAddress('');
    setProvider(null);
    setSigner(null);
    setSessionActive(false);
    setSessionEndTime(0);
    setRemainingTime('');
    setMintCount(0);
    setMintHistory([]);
    setError('');
    setSuccess('');
    setShowTaskModal(false);
    setCompletedTasks([]);
    setIsEligible(false);
    setNftBalance(0);
  };

  // ÊòæÁ§∫‰ªªÂä°Ê®°ÊÄÅÊ°Ü
  const showTasks = () => {
    setShowTaskModal(true);
  };

  // ÂÆåÊàê‰ªªÂä°
  const completeTask = (taskId: number) => {
    if (!completedTasks.includes(taskId)) {
      setCompletedTasks(prev => [...prev, taskId]);
      setSuccess(`‚úÖ ‰ªªÂä°"${tasks.find(t => t.id === taskId)?.title}"Â∑≤ÂÆåÊàêÔºÅ`);

      // Ê£ÄÊü•ÊòØÂê¶Êª°Ë∂≥ÂºÄÂêØ Session ÁöÑÊù°‰ª∂
      if (completedTasks.length + 1 >= 2) {
        setSuccess('üéâ ÊÅ≠ÂñúÔºÅÊÇ®Â∑≤ÂÆåÊàêË∂≥Â§ü‰ªªÂä°ÔºåÁé∞Âú®ÂèØ‰ª•ÂºÄÂêØ Gas SessionÔºÅ');
      }
    }
  };

  // ÂºÄÂêØ Gas Session
  const startGasSession = async () => {
    if (!signer || !userAddress) {
      setError('ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ');
      return;
    }

    if (!checkTaskCompletion()) {
      setError('ËØ∑ÂÖàÂÆåÊàêËá≥Â∞ë2‰∏™‰ªªÂä°ÊâçËÉΩÂºÄÂêØ Gas Session');
      return;
    }

    setError('');
    setSuccess('');

    try {
      console.log('ÂºÄÂêØ Gas Session...');

      // Ë∞ÉÁî®ÁúüÂÆûÁöÑÂêàÁ∫¶ÂáΩÊï∞
      const result = await startRealGasSession(userAddress, 120, signer); // 2ÂàÜÈíü

      if (result && result.hash) {
        const endTime = Math.floor(Date.now() / 1000) + 120;
        setSessionActive(true);
        setSessionEndTime(endTime);
        setSuccess(`‚úÖ Gas Session Â∑≤ÂºÄÂêØÔºÅÊÇ®Áé∞Âú®Êúâ 2 ÂàÜÈíüÁöÑÂÖçË¥π Gas ‰ΩìÈ™å„ÄÇ\n‰∫§ÊòìÂìàÂ∏å: ${result.hash}`);
        setShowTaskModal(false);

        console.log('Gas Session ÂºÄÂêØÊàêÂäüÔºå‰∫§ÊòìÂìàÂ∏å:', result.hash);
      } else {
        throw new Error('Gas Session ÂºÄÂêØÂ§±Ë¥•');
      }
    } catch (error: any) {
      console.error('ÂºÄÂêØ Session Â§±Ë¥•:', error);
      setError(error.message || 'ÂºÄÂêØ Session Â§±Ë¥•');
    }
  };

  // ÁúüÂÆû NFT Èì∏ÈÄ†
  const mintNFT = async () => {
    if (!signer || !userAddress) {
      setError('ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ');
      return;
    }

    setIsMinting(true);
    setError('');

    try {
      console.log('ÂºÄÂßãÈì∏ÈÄ† NFT...');

      // Ê£ÄÊü•ÊòØÂê¶ÊúâÂÖçË¥π GasÔºàÂü∫‰∫é‰ªªÂä°ÂÆåÊàêÂíå Session Áä∂ÊÄÅÔºâ
      const gasSponsored = sessionActive && checkTaskCompletion();

      // Ë∞ÉÁî®ÁúüÂÆûÁöÑÂêàÁ∫¶ÂáΩÊï∞
      const result = await mintNFTDirect(userAddress, signer, gasSponsored);

      // Ê∑ªÂä†Èì∏ÈÄ†ËÆ∞ÂΩï
      const newMint = {
        timestamp: Date.now(),
        hash: result.hash,
        gasSponsored: result.gasSponsored
      };

      setMintHistory(prev => [newMint, ...prev]);
      setMintCount(prev => prev + 1);

      // Êõ¥Êñ∞ NFT ‰ΩôÈ¢ù
      try {
        const newBalance = await getNFTBalance(userAddress);
        setNftBalance(newBalance);

        // ÈáçÊñ∞Ê£ÄÊü•ËµÑÊ†º
        const eligible = await checkEligibility(userAddress);
        setIsEligible(eligible);
      } catch (error) {
        console.error('Êõ¥Êñ∞‰ΩôÈ¢ùÂ§±Ë¥•:', error);
      }

      if (result.gasSponsored) {
        setSuccess(`üéâ NFT Èì∏ÈÄ†ÊàêÂäüÔºÅGas Â∑≤Áî± GasMorph ËµûÂä©ÔºÅ\n‰∫§ÊòìÂìàÂ∏å: ${result.hash}`);
      } else {
        setSuccess(`üéâ NFT Èì∏ÈÄ†ÊàêÂäüÔºÅÊÇ®ÊîØ‰ªò‰∫Ü Gas Ë¥πÁî®„ÄÇ\n‰∫§ÊòìÂìàÂ∏å: ${result.hash}`);
      }

      console.log('NFT Èì∏ÈÄ†ÊàêÂäüÔºåÂìàÂ∏å:', result.hash, 'GasËµûÂä©:', result.gasSponsored);

      // 3ÁßíÂêéÊ∏ÖÈô§ÊàêÂäüÊ∂àÊÅØ
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      console.error('Èì∏ÈÄ† NFT Â§±Ë¥•:', error);
      setError(error.message || 'Èì∏ÈÄ† NFT Â§±Ë¥•');
    } finally {
      setIsMinting(false);
    }
  };

  // ËÆ°ÁÆóÂâ©‰ΩôÊó∂Èó¥ÂíåÂÆöÊúüÂà∑Êñ∞Áä∂ÊÄÅ
  useEffect(() => {
    if (sessionActive && sessionEndTime > 0) {
      const interval = setInterval(() => {
        const now = Math.floor(Date.now() / 1000);
        const remaining = sessionEndTime - now;

        if (remaining <= 0) {
          setSessionActive(false);
          setSessionEndTime(0);
          setRemainingTime('');
          setError('Gas Session Â∑≤ËøáÊúü');
        } else {
          const minutes = Math.floor(remaining / 60);
          const seconds = remaining % 60;
          setRemainingTime(`${minutes}:${seconds.toString().padStart(2, '0')}`);
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [sessionActive, sessionEndTime]);

  // ÂÆöÊúüÂà∑Êñ∞Áî®Êà∑Áä∂ÊÄÅÔºàÊØè30ÁßíÔºâ
  useEffect(() => {
    if (isConnected && userAddress) {
      const refreshInterval = setInterval(async () => {
        try {
          // Âà∑Êñ∞ NFT ‰ΩôÈ¢ù
          const balance = await getNFTBalance(userAddress);
          setNftBalance(balance);

          // Âà∑Êñ∞ËµÑÊ†ºÁä∂ÊÄÅ
          const eligible = await checkEligibility(userAddress);
          setIsEligible(eligible);

          // Âà∑Êñ∞ Gas Session Áä∂ÊÄÅ
          const sessionStatus = await getSessionStatus(userAddress);
          if (sessionStatus.isActive && !sessionActive) {
            setSessionActive(true);
            setSessionEndTime(sessionStatus.expiryTime);
          } else if (!sessionStatus.isActive && sessionActive) {
            setSessionActive(false);
            setSessionEndTime(0);
            setRemainingTime('');
          }
        } catch (error) {
          console.error('Âà∑Êñ∞Áî®Êà∑Áä∂ÊÄÅÂ§±Ë¥•:', error);
        }
      }, 30000); // 30ÁßíÂà∑Êñ∞‰∏ÄÊ¨°

      return () => clearInterval(refreshInterval);
    }
  }, [isConnected, userAddress, sessionActive]);

  return (
    <div className="app">
      <div className="app-header">
        <div className="logo">
          <h1>‚õΩ GasMorph</h1>
          <p>ÊÑèÂõæÈ©±Âä®ÁöÑ Gas Ë°•Ë¥¥ÂçèËÆÆ</p>
        </div>
        <div className="network-info">
          <span className="network-badge">Hardhat Local</span>
        </div>
      </div>

      <div className="app-container">
        {!isConnected ? (
          <div className="welcome-section">
            <div className="welcome-card">
              <h2>üöÄ Ê¨¢Ëøé‰ΩøÁî® GasMorph</h2>
              <p>
                GasMorph ÊòØ‰∏Ä‰∏™ÂàõÊñ∞ÁöÑÊÑèÂõæÈ©±Âä® Gas Ë°•Ë¥¥ÂçèËÆÆÔºåÊó®Âú®Â∞Ü DApp ÁöÑ Gas ÊîØÂá∫
                ‰ªéÊàêÊú¨ËΩ¨Âèò‰∏∫ÂèØÈáèÂåñÁöÑÂ¢ûÈïøÊäïËµÑ„ÄÇ
              </p>
              <div className="demo-info">
                <h3>üéØ Demo ÂäüËÉΩ</h3>
                <ul>
                  <li><strong>Âä®ÊÄÅËßÑÂàô Demo:</strong> ÊåÅÊúâÁâπÂÆö NFT ÁöÑÂú∞ÂùÄ‰∫´Âèó Gas ÂÖ®ÂÖç</li>
                  <li><strong>‰ªªÂä°ÂÆåÊàê Demo:</strong> ÂÆåÊàê‰ªªÂä°ÂêéËé∑ÂæóÂÖçË¥π Gas ‰ΩìÈ™å</li>
                </ul>
              </div>
              <button onClick={handleConnect} className="connect-btn">
                ËøûÊé•Èí±ÂåÖ
              </button>
            </div>
          </div>
        ) : (
          <div className="main-content">
            {/* Áî®Êà∑‰ø°ÊÅØ */}
            <div className="user-info">
              <h3>üë§ Áî®Êà∑‰ø°ÊÅØ</h3>
              <p><strong>Âú∞ÂùÄ:</strong> {userAddress}</p>
              <p><strong>NFT ‰ΩôÈ¢ù:</strong> {nftBalance} ‰∏™</p>
              <p><strong>ËµÑÊ†ºÁä∂ÊÄÅ:</strong> 
                {isEligible ? 
                  <span className="eligible">‚úÖ Á¨¶Âêà Gas Ë°•Ë¥¥Êù°‰ª∂ (ÊåÅÊúâ NFT)</span> : 
                  <span className="not-eligible">‚ùå ‰∏çÁ¨¶ÂêàÊù°‰ª∂ (Êó† NFT)</span>
                }
              </p>
              <p><strong>‰ªªÂä°ÂÆåÊàê:</strong> {completedTasks.length}/3</p>
              <button onClick={handleDisconnect} className="disconnect-btn">
                Êñ≠ÂºÄËøûÊé•
              </button>
            </div>

            {/* Gas Session ÁÆ°ÁêÜ */}
            <div className="session-section">
              <h3>‚è∞ Gas Session ÁÆ°ÁêÜ</h3>
              {!sessionActive ? (
                <div className="session-inactive">
                  <p>ÂΩìÂâçÊ≤°ÊúâÊ¥ªË∑ÉÁöÑ Gas Session</p>
                  <div className="task-progress">
                    <p><strong>‰ªªÂä°ËøõÂ∫¶:</strong> {completedTasks.length}/3 ÂÆåÊàê</p>
                    {completedTasks.length < 2 && (
                      <p className="task-requirement">‚ö†Ô∏è ÈúÄË¶ÅÂÆåÊàêËá≥Â∞ë2‰∏™‰ªªÂä°ÊâçËÉΩÂºÄÂêØ Session</p>
                    )}
                  </div>
                  <div className="session-buttons">
                    <button onClick={showTasks} className="show-tasks-btn">
                      üìã Êü•Áúã‰ªªÂä°
                    </button>
                    <button 
                      onClick={startGasSession} 
                      disabled={!checkTaskCompletion()}
                      className="start-session-btn"
                    >
                      ÂºÄÂêØ 2 ÂàÜÈíüÂÖç Gas ‰ΩìÈ™å
                    </button>
                  </div>
                </div>
              ) : (
                <div className="session-active">
                  <div className="session-status">
                    <span className="status-indicator active"></span>
                    <span>Session Ê¥ªË∑É‰∏≠</span>
                  </div>
                  <p><strong>Ââ©‰ΩôÊó∂Èó¥:</strong> {remainingTime}</p>
                  <p><strong>ÁªìÊùüÊó∂Èó¥:</strong> {new Date(sessionEndTime * 1000).toLocaleString()}</p>
                </div>
              )}
            </div>

            {/* NFT Èì∏ÈÄ†Âå∫Âüü */}
            <div className="mint-section">
              <h3>üé® NFT Èì∏ÈÄ† Demo</h3>
              <div className="mint-info">
                <p><strong>Èì∏ÈÄ†Ê¨°Êï∞:</strong> {mintCount}</p>
                <p><strong>Gas Áä∂ÊÄÅ:</strong> 
                  {sessionActive && checkTaskCompletion() ? 
                    <span className="mint-available">üü¢ ÂÖçË¥πÈì∏ÈÄ† (Gas Â∑≤ËµûÂä©)</span> : 
                    <span className="mint-unavailable">üî¥ ÈúÄË¶ÅÊîØ‰ªò Gas Ë¥πÁî®</span>
                  }
                </p>
              </div>

              <button 
                onClick={mintNFT}
                disabled={isMinting}
                className="mint-btn"
              >
                {isMinting ? 'Èì∏ÈÄ†‰∏≠...' : 'üé® Mint NFT'}
              </button>

              {mintCount > 0 && (
                <div className="mint-history">
                  <h4>Èì∏ÈÄ†ÂéÜÂè≤</h4>
                  <div className="history-list">
                    {mintHistory.slice(0, 5).map((mint, index) => (
                      <div key={index} className="history-item">
                        <span>{new Date(mint.timestamp).toLocaleTimeString()}</span>
                        <span className="hash">{mint.hash.slice(0, 10)}...</span>
                        <span className={`status ${mint.gasSponsored ? 'sponsored' : 'paid'}`}>
                          {mint.gasSponsored ? '‚úÖ ÂÖçË¥π' : 'üí∞ ‰ªòË¥π'}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* ‰ªªÂä°Ê®°ÊÄÅÊ°Ü */}
            {showTaskModal && (
              <div className="modal-overlay">
                <div className="task-modal">
                  <div className="modal-header">
                    <h3>üìã ÂÆåÊàê‰ªªÂä°Ëé∑ÂæóÂÖçË¥π Gas</h3>
                    <button onClick={() => setShowTaskModal(false)} className="close-btn">√ó</button>
                  </div>
                  <div className="modal-content">
                    <p className="task-description">
                      ÂÆåÊàê‰ª•‰∏ã‰ªªÂä°‰∏≠ÁöÑËá≥Â∞ë2‰∏™ÔºåÂç≥ÂèØËé∑Âæó2ÂàÜÈíüÂÖçË¥π Gas ‰ΩìÈ™åÔºö
                    </p>
                    <div className="tasks-list">
                      {tasks.map(task => (
                        <div key={task.id} className={`task-item ${completedTasks.includes(task.id) ? 'completed' : ''}`}>
                          <div className="task-info">
                            <h4>{task.title}</h4>
                            <p>{task.description}</p>
                          </div>
                          <button
                            onClick={() => completeTask(task.id)}
                            disabled={completedTasks.includes(task.id)}
                            className={`task-btn ${completedTasks.includes(task.id) ? 'completed' : ''}`}
                          >
                            {completedTasks.includes(task.id) ? '‚úÖ Â∑≤ÂÆåÊàê' : 'ÂÆåÊàê'}
                          </button>
                        </div>
                      ))}
                    </div>
                    <div className="modal-footer">
                      <p className="task-progress-text">
                        Â∑≤ÂÆåÊàê: {completedTasks.length}/3 ‰ªªÂä°
                      </p>
                      {checkTaskCompletion() && (
                        <button onClick={startGasSession} className="start-session-btn">
