import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { MONAD_CONFIG } from '../utils/erc4337';

interface ConnectWalletProps {
  onConnect: (address: string, provider: ethers.BrowserProvider) => void;
  onDisconnect: () => void;
  isConnected: boolean;
  userAddress?: string;
}

const ConnectWallet: React.FC<ConnectWalletProps> = ({
  onConnect,
  onDisconnect,
  isConnected,
  userAddress
}) => {
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState<string>('');

  // 检查是否已安装 MetaMask
  const checkIfWalletIsInstalled = (): boolean => {
    return typeof window !== 'undefined' && typeof window.ethereum !== 'undefined';
  };

  // 检查是否连接到正确的网络
  const checkNetwork = async (provider: ethers.BrowserProvider): Promise<boolean> => {
    try {
      const network = await provider.getNetwork();
      return network.chainId === BigInt(MONAD_CONFIG.chainId);
    } catch (error) {
      console.error('检查网络失败:', error);
      return false;
    }
  };

  // 切换到 Monad 测试网
  const switchToMonadTestnet = async (): Promise<boolean> => {
    if (!window.ethereum) return false;

    try {
      await window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: `0x${MONAD_CONFIG.chainId.toString(16)}` }],
      });
      return true;
    } catch (switchError: any) {
      // 如果网络不存在，尝试添加网络
      if (switchError.code === 4902) {
        try {
          await window.ethereum.request({
            method: 'wallet_addEthereumChain',
            params: [
              {
                chainId: `0x${MONAD_CONFIG.chainId.toString(16)}`,
                chainName: 'Monad Testnet',
                nativeCurrency: {
                  name: 'MONAD',
                  symbol: 'MONAD',
                  decimals: 18,
                },
                rpcUrls: [MONAD_CONFIG.rpcUrl],
                blockExplorerUrls: ['https://explorer.testnet.monad.xyz/'],
              },
            ],
          });
          return true;
        } catch (addError) {
          console.error('添加网络失败:', addError);
          return false;
        }
      }
      return false;
    }
  };

  // 连接钱包
  const connectWallet = async () => {
    if (!checkIfWalletIsInstalled()) {
      setError('请先安装 MetaMask 钱包');
      return;
    }

    setIsConnecting(true);
    setError('');

    try {
      // 请求连接钱包
      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts',
      });

      if (accounts.length === 0) {
        throw new Error('没有找到账户');
      }

      const userAddress = accounts[0];
      const provider = new ethers.BrowserProvider(window.ethereum);

      // 检查网络
      const isCorrectNetwork = await checkNetwork(provider);
      if (!isCorrectNetwork) {
        const switched = await switchToMonadTestnet();
        if (!switched) {
          throw new Error('请切换到 Monad 测试网');
        }
      }

      onConnect(userAddress, provider);
    } catch (error: any) {
      console.error('连接钱包失败:', error);
      setError(error.message || '连接钱包失败');
    } finally {
      setIsConnecting(false);
    }
  };

  // 断开连接
  const disconnectWallet = () => {
    onDisconnect();
  };

  // 监听账户变化
  useEffect(() => {
    if (typeof window !== 'undefined' && window.ethereum) {
      const handleAccountsChanged = (accounts: string[]) => {
        if (accounts.length === 0) {
          onDisconnect();
        } else if (isConnected && accounts[0] !== userAddress) {
          // 账户切换，重新连接
          const provider = new ethers.BrowserProvider(window.ethereum);
          onConnect(accounts[0], provider);
        }
      };

      const handleChainChanged = () => {
        // 网络切换时重新检查
        window.location.reload();
      };

      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);

      return () => {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, [isConnected, userAddress, onConnect, onDisconnect]);

  return (
    <div className="connect-wallet">
      <div className="wallet-status">
        {isConnected ? (
          <div className="connected">
            <div className="status-indicator connected"></div>
            <span className="status-text">已连接</span>
            <div className="address">
              {userAddress?.slice(0, 6)}...{userAddress?.slice(-4)}
            </div>
            <button 
              onClick={disconnectWallet}
              className="disconnect-btn"
            >
              断开连接
            </button>
          </div>
        ) : (
          <div className="disconnected">
            <div className="status-indicator disconnected"></div>
            <span className="status-text">未连接</span>
            <button 
              onClick={connectWallet}
              disabled={isConnecting}
              className="connect-btn"
            >
              {isConnecting ? '连接中...' : '连接钱包'}
            </button>
          </div>
        )}
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <style jsx>{`
        .connect-wallet {
          padding: 20px;
          border-radius: 12px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          margin-bottom: 20px;
        }

        .wallet-status {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }

        .connected, .disconnected {
          display: flex;
          align-items: center;
          gap: 12px;
          width: 100%;
        }

        .status-indicator {
          width: 12px;
          height: 12px;
          border-radius: 50%;
        }

        .status-indicator.connected {
          background-color: #4ade80;
          box-shadow: 0 0 8px rgba(74, 222, 128, 0.5);
        }

        .status-indicator.disconnected {
          background-color: #f87171;
          box-shadow: 0 0 8px rgba(248, 113, 113, 0.5);
        }

        .status-text {
          font-weight: 600;
          font-size: 16px;
        }

        .address {
          background: rgba(255, 255, 255, 0.1);
          padding: 4px 8px;
          border-radius: 6px;
          font-family: 'Courier New', monospace;
          font-size: 14px;
        }

        .connect-btn, .disconnect-btn {
          padding: 8px 16px;
          border: none;
          border-radius: 8px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }

        .connect-btn {
          background: #4ade80;
          color: white;
        }

        .connect-btn:hover:not(:disabled) {
          background: #22c55e;
          transform: translateY(-1px);
        }

        .connect-btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .disconnect-btn {
          background: rgba(255, 255, 255, 0.2);
          color: white;
        }

        .disconnect-btn:hover {
          background: rgba(255, 255, 255, 0.3);
        }

        .error-message {
          margin-top: 12px;
          padding: 8px 12px;
          background: rgba(248, 113, 113, 0.2);
          border: 1px solid rgba(248, 113, 113, 0.3);
          border-radius: 6px;
          color: #fecaca;
          font-size: 14px;
        }
      `}</style>
    </div>
  );
};

export default ConnectWallet;
