import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { 
  createMintTransaction, 
  createSponsoredUserOp, 
  sendSponsoredUserOp, 
  getNFTBalance,
  PaymasterMode 
} from '../utils/erc4337';

interface NFTMintCardProps {
  userAddress: string;
  provider: ethers.BrowserProvider;
  sessionMode: PaymasterMode;
}

const NFTMintCard: React.FC<NFTMintCardProps> = ({
  userAddress,
  provider,
  sessionMode
}) => {
  const [nftBalance, setNftBalance] = useState<number>(0);
  const [mintPrice, setMintPrice] = useState<string>('');
  const [isMinting, setIsMinting] = useState(false);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [txHash, setTxHash] = useState<string>('');

  // Ëé∑Âèñ NFT ‰ΩôÈ¢ùÂíåÈì∏ÈÄ†‰ª∑Ê†º
  const fetchNFTInfo = async () => {
    try {
      const balance = await getNFTBalance(userAddress);
      setNftBalance(balance);

      // Ëé∑ÂèñÈì∏ÈÄ†‰ª∑Ê†ºÔºàËøôÈáåÁÆÄÂåñÂ§ÑÁêÜÔºåÂÆûÈôÖÂ∫îËØ•‰ªéÂêàÁ∫¶Ëé∑ÂèñÔºâ
      setMintPrice('0.001 ETH');
    } catch (error) {
      console.error('Ëé∑Âèñ NFT ‰ø°ÊÅØÂ§±Ë¥•:', error);
    }
  };

  // Èì∏ÈÄ† NFT
  const handleMintNFT = async () => {
    setIsMinting(true);
    setError('');
    setSuccess('');
    setTxHash('');

    try {
      // ÂàõÂª∫Èì∏ÈÄ†‰∫§Êòì
      const mintTx = await createMintTransaction(userAddress);

      // ÂàõÂª∫ËµûÂä©ÁöÑ UserOperation
      const userOp = await createSponsoredUserOp(mintTx, userAddress, sessionMode);

      // ÂèëÈÄÅ UserOperation
      const hash = await sendSponsoredUserOp(userOp);
      setTxHash(hash);

      setSuccess(`üéâ Mint ÊàêÂäüÔºÅGas Áî± GasMorph ËµûÂä©ÔºÅ\n‰∫§ÊòìÂìàÂ∏å: ${hash}`);

      // ÈáçÊñ∞Ëé∑Âèñ NFT ‰ΩôÈ¢ù
      await fetchNFTInfo();
    } catch (error: any) {
      console.error('Èì∏ÈÄ† NFT Â§±Ë¥•:', error);
      setError(error.message || 'Èì∏ÈÄ† NFT Â§±Ë¥•');
    } finally {
      setIsMinting(false);
    }
  };

  // ÂàùÂßãÂä†ËΩΩ
  useEffect(() => {
    fetchNFTInfo();
  }, [userAddress]);

  return (
    <div className="nft-mint-card">
      <div className="nft-header">
        <div className="nft-image">
          <div className="nft-placeholder">
            üé®
          </div>
        </div>
        <div className="nft-info">
          <h3>GasMorph Demo NFT</h3>
          <p className="nft-description">
            ÊåÅÊúâÊ≠§ NFT ÁöÑÁî®Êà∑ÂèØ‰ª•‰∫´Âèó GasMorph ÁöÑ Gas Ë°•Ë¥¥ÊúçÂä°
          </p>
          <div className="nft-stats">
            <div className="stat">
              <span className="label">ÊÇ®ÁöÑ‰ΩôÈ¢ù:</span>
              <span className="value">{nftBalance} NFT</span>
            </div>
            <div className="stat">
              <span className="label">Èì∏ÈÄ†‰ª∑Ê†º:</span>
              <span className="value">{mintPrice}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="mint-section">
        <div className="sponsorship-info">
          <div className="sponsorship-badge">
            {sessionMode === 'nft' ? 'üéØ NFT ÊåÅÊúâËÄÖÊ®°Âºè' : '‚è∞ Session Ê®°Âºè'}
          </div>
          <p className="sponsorship-description">
            {sessionMode === 'nft' 
              ? 'Âü∫‰∫éÊÇ®ÊåÅÊúâÁöÑ NFTÔºåGasMorph Â∞ÜËµûÂä©Êú¨Ê¨°‰∫§ÊòìÁöÑ Gas Ë¥πÁî®'
              : 'Âú®ÊÇ®ÁöÑ Gas Session ÊúüÈó¥ÔºåGasMorph Â∞ÜËµûÂä©Êú¨Ê¨°‰∫§ÊòìÁöÑ Gas Ë¥πÁî®'
            }
          </p>
        </div>

        <button
          onClick={handleMintNFT}
          disabled={isMinting}
          className="mint-btn"
        >
          {isMinting ? 'Èì∏ÈÄ†‰∏≠...' : 'üé® Mint NFT'}
        </button>
      </div>

      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          <div>‚úÖ {success}</div>
          {txHash && (
            <div className="tx-hash">
              <a 
                href={`https://explorer.testnet.monad.xyz/tx/${txHash}`}
                target="_blank"
                rel="noopener noreferrer"
                className="tx-link"
              >
                Âú®Âå∫ÂùóÊµèËßàÂô®‰∏≠Êü•Áúã ‚Üí
              </a>
            </div>
          )}
        </div>
      )}

      <div className="features-section">
        <h4>üéÅ NFT ÊåÅÊúâËÄÖÁâπÊùÉ</h4>
        <ul className="features-list">
          <li>‚úÖ ‰∫´Âèó GasMorph ÁöÑ Gas Ë°•Ë¥¥ÊúçÂä°</li>
          <li>‚úÖ ‰ºòÂÖàËé∑ÂæóÊñ∞ÂäüËÉΩÊµãËØïËµÑÊ†º</li>
          <li>‚úÖ ÂèÇ‰∏éÁ§æÂå∫Ê≤ªÁêÜÊäïÁ•®</li>
          <li>‚úÖ Ëé∑ÂæóÁã¨ÂÆ∂Á©∫ÊäïÊú∫‰ºö</li>
        </ul>
      </div>

      <style jsx>{`
        .nft-mint-card {
          padding: 24px;
          border-radius: 16px;
          background: linear-gradient(135deg, #a8edea 0%, #fed6e3 100%);
          color: #1f2937;
          margin-bottom: 20px;
        }

        .nft-header {
          display: flex;
          gap: 20px;
          margin-bottom: 24px;
        }

        .nft-image {
          flex-shrink: 0;
        }

        .nft-placeholder {
          width: 120px;
          height: 120px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          border-radius: 16px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 48px;
          color: white;
          box-shadow: 0 8px 32px rgba(102, 126, 234, 0.3);
        }

        .nft-info {
          flex: 1;
        }

        .nft-info h3 {
          margin: 0 0 8px 0;
          font-size: 24px;
          font-weight: 700;
          color: #1f2937;
        }

        .nft-description {
