import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

// 硬编码的规则：持有特定 NFT 的地址
const ELIGIBLE_ADDRESSES = [
  '0x742d35Cc6634C0532925a3b8D4C9db96C4b4d8b6', // 示例地址
  '0x1234567890123456789012345678901234567890', // 示例地址
];

// 模拟的 NFT 合约地址
const DEMO_NFT_ADDRESS = '0x0000000000000000000000000000000000000000';

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [userAddress, setUserAddress] = useState<string>('');
  const [provider, setProvider] = useState<ethers.BrowserProvider | null>(null);
  const [signer, setSigner] = useState<ethers.Signer | null>(null);
  const [sessionActive, setSessionActive] = useState(false);
  const [sessionEndTime, setSessionEndTime] = useState<number>(0);
  const [remainingTime, setRemainingTime] = useState<string>('');
  const [mintCount, setMintCount] = useState(0);
  const [isMinting, setIsMinting] = useState(false);
  const [mintHistory, setMintHistory] = useState<Array<{timestamp: number, hash: string}>>([]);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');

  // 检查用户是否有资格享受 Gas 补贴
  const checkEligibility = (address: string): boolean => {
    return ELIGIBLE_ADDRESSES.includes(address.toLowerCase());
  };

  // 连接钱包
  const handleConnect = async () => {
    setError('');
    setSuccess('');

    if (typeof window !== 'undefined' && (window as any).ethereum) {
      try {
        console.log('正在连接钱包...');
        const accounts = await (window as any).ethereum.request({
          method: 'eth_requestAccounts',
        });

        if (accounts.length > 0) {
          const address = accounts[0];
          console.log('钱包地址:', address);

          const prov = new ethers.BrowserProvider((window as any).ethereum);
          const signerInstance = await prov.getSigner();

          setUserAddress(address);
          setProvider(prov);
          setSigner(signerInstance);
          setIsConnected(true);

          setSuccess('钱包连接成功！');
          console.log('钱包连接成功');
        }
      } catch (error: any) {
        console.error('连接钱包失败:', error);
        setError(error.message || '连接钱包失败');
      }
    } else {
      setError('请安装 MetaMask 钱包');
    }
  };

  // 断开连接
  const handleDisconnect = () => {
    setIsConnected(false);
    setUserAddress('');
    setProvider(null);
    setSigner(null);
    setSessionActive(false);
    setSessionEndTime(0);
    setRemainingTime('');
    setMintCount(0);
    setMintHistory([]);
    setError('');
    setSuccess('');
  };

  // 开启 Gas Session
  const startGasSession = async () => {
    if (!signer) {
      setError('请先连接钱包');
      return;
    }

    setError('');
    setSuccess('');

    try {
      console.log('开启 Gas Session...');

      // 模拟开启 Session（实际应该调用合约）
      const endTime = Math.floor(Date.now() / 1000) + 120; // 2分钟
      setSessionActive(true);
      setSessionEndTime(endTime);
      setSuccess('Gas Session 已开启！您现在有 2 分钟的免费 Gas 体验。');

      console.log('Gas Session 开启成功，结束时间:', new Date(endTime * 1000));
    } catch (error: any) {
      console.error('开启 Session 失败:', error);
      setError(error.message || '开启 Session 失败');
    }
  };

  // 模拟 NFT 铸造
  const mintNFT = async () => {
    if (!signer || !userAddress) {
      setError('请先连接钱包');
      return;
    }

    if (!sessionActive) {
      setError('请先开启 Gas Session');
      return;
    }

    setIsMinting(true);
    setError('');

    try {
      console.log('开始铸造 NFT...');

      // 模拟交易（实际应该调用合约）
      const mockHash = `0x${Math.random().toString(16).substr(2, 64)}`;

      // 添加铸造记录
      const newMint = {
        timestamp: Date.now(),
        hash: mockHash
      };

      setMintHistory(prev => [newMint, ...prev]);
      setMintCount(prev => prev + 1);
      setSuccess(`🎉 NFT 铸造成功！Gas 已由 GasMorph 赞助！\n交易哈希: ${mockHash}`);

      console.log('NFT 铸造成功，哈希:', mockHash);

      // 3秒后清除成功消息
      setTimeout(() => setSuccess(''), 3000);
    } catch (error: any) {
      console.error('铸造 NFT 失败:', error);
      setError(error.message || '铸造 NFT 失败');
    } finally {
      setIsMinting(false);
    }
  };

  // 计算剩余时间
  useEffect(() => {
    if (sessionActive && sessionEndTime > 0) {
      const interval = setInterval(() => {
        const now = Math.floor(Date.now() / 1000);
        const remaining = sessionEndTime - now;

        if (remaining <= 0) {
          setSessionActive(false);
          setSessionEndTime(0);
          setRemainingTime('');
          setError('Gas Session 已过期');
        } else {
          const minutes = Math.floor(remaining / 60);
          const seconds = remaining % 60;
          setRemainingTime(`${minutes}:${seconds.toString().padStart(2, '0')}`);
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [sessionActive, sessionEndTime]);

  return (
    <div className="app">
      <div className="app-header">
        <div className="logo">
          <h1>⛽ GasMorph</h1>
          <p>意图驱动的 Gas 补贴协议</p>
        </div>
        <div className="network-info">
          <span className="network-badge">Monad Testnet</span>
        </div>
      </div>

      <div className="app-container">
        {!isConnected ? (
          <div className="welcome-section">
            <div className="welcome-card">
              <h2>🚀 欢迎使用 GasMorph</h2>
              <p>
                GasMorph 是一个创新的意图驱动 Gas 补贴协议，旨在将 DApp 的 Gas 支出
                从成本转变为可量化的增长投资。
              </p>
              <div className="demo-info">
                <h3>🎯 Demo 功能</h3>
                <ul>
                  <li><strong>动态规则 Demo:</strong> 持有特定 NFT 的地址享受 Gas 全免</li>
                  <li><strong>Gas Session Demo:</strong> 2分钟免费 Gas 体验</li>
                </ul>
              </div>
              <button onClick={handleConnect} className="connect-btn">
                连接钱包
              </button>
            </div>
          </div>
        ) : (
          <div className="main-content">
            {/* 用户信息 */}
            <div className="user-info">
              <h3>👤 用户信息</h3>
              <p><strong>地址:</strong> {userAddress}</p>
              <p><strong>资格状态:</strong> 
                {checkEligibility(userAddress) ? 
                  <span className="eligible">✅ 符合 Gas 补贴条件</span> : 
                  <span className="not-eligible">❌ 不符合条件</span>
                }
              </p>
              <button onClick={handleDisconnect} className="disconnect-btn">
                断开连接
              </button>
            </div>

            {/* Gas Session 管理 */}
            <div className="session-section">
              <h3>⏰ Gas Session 管理</h3>
              {!sessionActive ? (
                <div className="session-inactive">
                  <p>当前没有活跃的 Gas Session</p>
                  <button onClick={startGasSession} className="start-session-btn">
                    开启 2 分钟免 Gas 体验
                  </button>
                </div>
              ) : (
                <div className="session-active">
                  <div className="session-status">
                    <span className="status-indicator active"></span>
                    <span>Session 活跃中</span>
                  </div>
                  <p><strong>剩余时间:</strong> {remainingTime}</p>
                  <p><strong>结束时间:</strong> {new Date(sessionEndTime * 1000).toLocaleString()}</p>
                </div>
              )}
            </div>

            {/* NFT 铸造区域 */}
            <div className="mint-section">
              <h3>🎨 NFT 铸造 Demo</h3>
              <div className="mint-info">
                <p><strong>铸造次数:</strong> {mintCount}</p>
                <p><strong>状态:</strong> 
                  {sessionActive ? 
                    <span className="mint-available">🟢 可以铸造 (Gas 已赞助)</span> : 
                    <span className="mint-unavailable">🔴 需要开启 Session</span>
                  }
                </p>
              </div>

              <button 
                onClick={mintNFT}
                disabled={!sessionActive || isMinting}
                className="mint-btn"
              >
                {isMinting ? '铸造中...' : '🎨 Mint NFT'}
              </button>

              {mintCount > 0 && (
                <div className="mint-history">
                  <h4>铸造历史</h4>
                  <div className="history-list">
                    {mintHistory.slice(0, 5).map((mint, index) => (
                      <div key={index} className="history-item">
                        <span>{new Date(mint.timestamp).toLocaleTimeString()}</span>
                        <span className="hash">{mint.hash.slice(0, 10)}...</span>
                        <span className="status">✅ 成功</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* 错误和成功消息 */}
            {error && (
              <div className="message error">
                ❌ {error}
              </div>
            )}
            {success && (
              <div className="message success">
                ✅ {success}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
