import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { getSessionStatus, startGasSession } from '../utils/erc4337';

interface GasSessionManagerProps {
  userAddress: string;
  provider: ethers.BrowserProvider;
  signer?: ethers.Signer;
}

const GasSessionManager: React.FC<GasSessionManagerProps> = ({
  userAddress,
  provider,
  signer
}) => {
  const [sessionStatus, setSessionStatus] = useState<{
    isActive: boolean;
    expiryTime: number;
  } | null>(null);
  const [remainingTime, setRemainingTime] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');

  // Ê£ÄÊü• Session Áä∂ÊÄÅ
  const checkSessionStatus = async () => {
    try {
      const status = await getSessionStatus(userAddress);
      setSessionStatus(status);
    } catch (error) {
      console.error('Ê£ÄÊü• Session Áä∂ÊÄÅÂ§±Ë¥•:', error);
    }
  };

  // ÂºÄÂêØ Gas Session
  const handleStartSession = async () => {
    if (!signer) {
      setError('ÈúÄË¶ÅÁ≠æÂêçËÄÖÊùÉÈôêÊù•ÂºÄÂêØ Session');
      return;
    }

    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      const tx = await startGasSession(userAddress, 120, signer); // 2 ÂàÜÈíü
      await tx.wait();

      setSuccess('Gas Session Â∑≤ÂºÄÂêØÔºÅÊÇ®Áé∞Âú®Êúâ 2 ÂàÜÈíüÁöÑÂÖçË¥π Gas ‰ΩìÈ™å„ÄÇ');

      // ÈáçÊñ∞Ê£ÄÊü•Áä∂ÊÄÅ
      await checkSessionStatus();
    } catch (error: any) {
      console.error('ÂºÄÂêØ Session Â§±Ë¥•:', error);
      setError(error.message || 'ÂºÄÂêØ Session Â§±Ë¥•');
    } finally {
      setIsLoading(false);
    }
  };

  // ËÆ°ÁÆóÂâ©‰ΩôÊó∂Èó¥
  const calculateRemainingTime = () => {
    if (!sessionStatus || !sessionStatus.isActive) {
      setRemainingTime('');
      return;
    }

    const now = Math.floor(Date.now() / 1000);
    const remaining = sessionStatus.expiryTime - now;

    if (remaining <= 0) {
      setRemainingTime('Â∑≤ËøáÊúü');
      setSessionStatus({ ...sessionStatus, isActive: false });
      return;
    }

    const minutes = Math.floor(remaining / 60);
    const seconds = remaining % 60;
    setRemainingTime(`${minutes}:${seconds.toString().padStart(2, '0')}`);
  };

  // ÂÆöÊúüÊõ¥Êñ∞Ââ©‰ΩôÊó∂Èó¥
  useEffect(() => {
    if (sessionStatus?.isActive) {
      calculateRemainingTime();
      const interval = setInterval(calculateRemainingTime, 1000);
      return () => clearInterval(interval);
    }
  }, [sessionStatus]);

  // ÂàùÂßãÊ£ÄÊü•Áä∂ÊÄÅ
  useEffect(() => {
    checkSessionStatus();
  }, [userAddress]);

  return (
    <div className="gas-session-manager">
      <div className="session-header">
        <h3>‚è∞ Gas Session ÁÆ°ÁêÜ</h3>
        <div className="session-status">
          {sessionStatus?.isActive ? (
            <div className="status active">
              <div className="status-dot active"></div>
              <span>Session Ê¥ªË∑É</span>
            </div>
          ) : (
            <div className="status inactive">
              <div className="status-dot inactive"></div>
              <span>Session Êú™ÊøÄÊ¥ª</span>
            </div>
          )}
        </div>
      </div>

      {sessionStatus?.isActive && (
        <div className="session-info">
          <div className="remaining-time">
            <span className="label">Ââ©‰ΩôÊó∂Èó¥:</span>
            <span className="time">{remainingTime}</span>
          </div>
          <div className="expiry-info">
            <span className="label">ËøáÊúüÊó∂Èó¥:</span>
            <span className="time">
              {new Date(sessionStatus.expiryTime * 1000).toLocaleString()}
            </span>
          </div>
        </div>
      )}

      <div className="session-actions">
        {!sessionStatus?.isActive ? (
          <button
            onClick={handleStartSession}
            disabled={isLoading || !signer}
            className="start-session-btn"
          >
            {isLoading ? 'ÂºÄÂêØ‰∏≠...' : 'ÂºÄÂêØ 2 ÂàÜÈíüÂÖç Gas ‰ΩìÈ™å'}
          </button>
        ) : (
          <div className="session-active-message">
            üéâ ÊÇ®ÁöÑ Gas Session Ê≠£Âú®ËøêË°å‰∏≠ÔºÅ
          </div>
        )}
      </div>

      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          ‚úÖ {success}
        </div>
      )}

      <div className="session-description">
        <p>
          <strong>Gas Session ÊòØ‰ªÄ‰πàÔºü</strong><br/>
          Gas Session ÊòØ GasMorph Êèê‰æõÁöÑÈôêÊó∂ÂÖçË¥π Gas ‰ΩìÈ™åÂäüËÉΩ„ÄÇ
          Âú® Session ÊúüÈó¥ÔºåÊÇ®ÁöÑÊâÄÊúâ‰∫§ÊòìÈÉΩÂ∞ÜÁî± GasMorph ËµûÂä© Gas Ë¥πÁî®„ÄÇ
        </p>
      </div>

      <style jsx>{`
        .gas-session-manager {
          padding: 24px;
          border-radius: 16px;
          background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
          color: white;
          margin-bottom: 20px;
        }

        .session-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 20px;
        }

        .session-header h3 {
          margin: 0;
          font-size: 20px;
          font-weight: 700;
        }

        .session-status {
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .status {
          display: flex;
          align-items: center;
          gap: 6px;
          font-size: 14px;
          font-weight: 600;
        }

        .status-dot {
          width: 8px;
          height: 8px;
          border-radius: 50%;
        }

        .status-dot.active {
          background-color: #4ade80;
          box-shadow: 0 0 6px rgba(74, 222, 128, 0.6);
        }

        .status-dot.inactive {
          background-color: #f87171;
          box-shadow: 0 0 6px rgba(248, 113, 113, 0.6);
        }

        .session-info {
          background: rgba(255, 255, 255, 0.1);
          padding: 16px;
          border-radius: 12px;
          margin-bottom: 20px;
        }

        .remaining-time, .expiry-info {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 8px;
        }

        .remaining-time:last-child, .expiry-info:last-child {
          margin-bottom: 0;
        }

        .label {
          font-weight: 600;
          font-size: 14px;
        }

        .time {
          font-family: 'Courier New', monospace;
          font-weight: 700;
          font-size: 16px;
          color: #fbbf24;
        }

        .session-actions {
          margin-bottom: 20px;
        }

        .start-session-btn {
          width: 100%;
          padding: 12px 24px;
          background: linear-gradient(135deg, #4ade80 0%, #22c55e 100%);
          color: white;
          border: none;
          border-radius: 12px;
          font-size: 16px;
          font-weight: 700;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .start-session-btn:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 8px 25px rgba(74, 222, 128, 0.3);
        }

        .start-session-btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
          transform: none;
        }

        .session-active-message {
          text-align: center;
          padding: 16px;
          background: rgba(74, 222, 128, 0.2);
          border: 2px solid rgba(74, 222, 128, 0.3);
          border-radius: 12px;
          font-weight: 700;
          font-size: 16px;
        }

        .error-message, .success-message {
          padding: 12px 16px;
          border-radius: 8px;
          margin-bottom: 16px;
          font-weight: 600;
        }

        .error-message {
          background: rgba(248, 113, 113, 0.2);
          border: 1px solid rgba(248, 113, 113, 0.3);
          color: #fecaca;
        }

        .success-message {
          background: rgba(74, 222, 128, 0.2);
          border: 1px solid rgba(74, 222, 128, 0.3);
          color: #dcfce7;
        }

        .session-description {
          background: rgba(255, 255, 255, 0.1);
          padding: 16px;
          border-radius: 12px;
          font-size: 14px;
          line-height: 1.6;
        }

        .session-description p {
          margin: 0;
        }

        .session-description strong {
          color: #fbbf24;
        }
      `}</style>
    </div>
  );
};

export default GasSessionManager;
